mapper.insert:
INSERT INTO loan_application (
    user_id, loan_amount, loan_period, repayment_method, interest_rate,
    loan_purpose, actual_loan_amount, remark, status, apply_time,
    approve_time, update_time, reviewer_id
) VALUES (
    #{userId}, #{loanAmount}, #{loanPeriod}, #{repaymentMethod}, #{interestRate},
    #{loanPurpose}, #{actualLoanAmount}, #{remark}, #{status}, #{applyTime},
    #{approveTime}, #{updateTime}, #{reviewerId}
)

mapper.update:
UPDATE loan_application
loan_amount = #{loanAmount}, loan_period = #{loanPeriod}, repayment_method = #{repaymentMethod}, interest_rate = #{interestRate}, loan_purpose = #{loanPurpose}, actual_loan_amount = #{actualLoanAmount}, remark = #{remark}, status = #{status}, approve_time = #{approveTime}, reviewer_id = #{reviewerId}, update_time = #{updateTime}
WHERE id = #{id}

mapper.select:
SELECT * FROM loan_application
WHERE user_id = #{userId}
ORDER BY apply_time DESC

mapper.insert:
INSERT INTO repayment_schedule (
    loan_id, user_id, installment_no, amount_due,
    due_date, status, update_time
) VALUES (
    #{loanId}, #{userId}, #{installmentNo}, #{amountDue},
    #{dueDate}, #{status}, #{updateTime}
)

INSERT INTO repayment_schedule (
    loan_id, user_id, installment_no, amount_due,
    due_date, status, update_time
) VALUES

    (
    #{item.loanId}, #{item.userId}, #{item.installmentNo}, #{item.amountDue},
    #{item.dueDate}, #{item.status}, #{item.updateTime}
    )

mapper.select:
SELECT * FROM repayment_schedule
WHERE loan_id = #{loanId}
ORDER BY installment_no

mapper.insert:
INSERT INTO actual_repayment (
    loan_id, user_id, installment_no, repayment_amount,
    repayment_time, status, actual_repayment_time, update_time
) VALUES (
    #{loanId}, #{userId}, #{installmentNo}, #{repaymentAmount},
    #{repaymentTime}, #{status}, #{actualRepaymentTime}, #{updateTime}
)

mapper.select:
SELECT * FROM actual_repayment
WHERE user_id = #{userId}
ORDER BY actual_repayment_time DESC
